from net.sf.jasperreports.engine import JasperFillManager, JREmptyDataSource, JRExporterParameter
from net.sf.jasperreports.engine.export import JRPdfExporter
from java.util import HashMap
import os.path

def produce(infile, outfile):
    hm = HashMap()
    # Fill the report using an empty data source
    data = JasperFillManager.fillReport(infile, hm, JREmptyDataSource())
    # Create a PDF exporter
    exporter = JRPdfExporter()
    # Configure the exporter (set output file name and print object)
    exporter.setParameter(JRExporterParameter.OUTPUT_FILE_NAME, outfile)
    exporter.setParameter(JRExporterParameter.JASPER_PRINT, data)
    #Export the PDF file
    exporter.exportReport()

if __name__ == "__main__":
    produce(os.path.dirname(__file__) + "/simple.jasper", os.path.dirname(__file__) + "/simple.pdf")
